'use strict';

const MetroHash64  = require('./index').MetroHash64;
const MetroHash128 = require('./index').MetroHash128;
const metrohash64  = require('./index').metrohash64;
const metrohash128 = require('./index').metrohash128;
const TESTVECTOR_S = '012345678901234567890123456789012345678901234567890123456789012';

suite('metrohash', () => {

  benchmark('MetroHash64', () => {
    let hasher = new MetroHash64();
    hasher.update(TESTVECTOR_S);
    return hasher.digest();
  });

  benchmark('MetroHash128', () => {
    let hasher = new MetroHash128();
    hasher.update(TESTVECTOR_S);
    return hasher.digest();
  });

  benchmark('metrohash64', () => {
    return metrohash64(TESTVECTOR_S);
  });

  benchmark('metrohash128', () => {
    return metrohash128(TESTVECTOR_S);
  });

});
